user_id,user_name,user_account,user_phone,user_sex,user_grade,user_state
from user


        and user_id = #{userId}


        and user_phone = #{phone}


        and user_name like concat('%',#{username},'%')


limit ${size * (page-1)},${size}

select
count(1)
from user


        and user_id = #{userId}


        and user_phone = #{phone}


        and user_name like concat('%',#{username},'%')
